import fileio from '@ohos.fileio';
import ctx from '@ohos.application.context';

@Entry
@Component
export struct HopMotion {
  private context: ctx.AbilityContext = getContext(this) as ctx.AbilityContext
  private rawfilePath = this.context.bundleCodeDir + '/entry/resources/rawfile/icon-motion'
  @State iconMotionArr: Array<ImageFrameInfo> = []

  aboutToAppear() {
    this.getRawFileDir()
  }

  getRawFileDir() {
    fileio.opendir(this.rawfilePath).then(async (dir) => {
      let dirent = dir.readSync();
      let images = []
      while (dirent && dirent.isFile()) {
        images.push({
          src: $rawfile(`icon-motion/${dirent.name}`)
        });
        dirent = dir.readSync();
      }
      this.iconMotionArr = [...images]
    }).catch(function(err){
      console.info("opendir failed with error:"+ err);
    })
  }

  build() {
    Row() {
      Column() {
        if (this.iconMotionArr.length > 0) {
          ImageAnimator()
            .images(this.iconMotionArr)
            .duration(22)
            .width(240).height(240)
            .fixedSize(true)
            .state(AnimationStatus.Running)
            .iterations(-1)
        }
      }
      .width('100%')
    }
    .height('100%')
  }
}

export class FileInfo {
  path: string;
  name: string;
  size: number;
  bytes: Uint8Array;
  uri: string;

  constructor(path: string, name: string, size: number, bytes: Uint8Array, uri: string) {
    this.path = path;
    this.name = name;
    this.size = size;
    this.bytes = bytes;
    this.uri = uri;
  }

  static Builder(): FileInfoBuilder {
    return new FileInfoBuilder();
  }

  toMap(): Map<string, Object> {
    let data: Map<string, Object> = new Map();
    data.set("path", this.path);
    data.set("name", this.name);
    data.set("size", this.size);
    data.set("bytes", this.bytes);
    data.set("identifier", this.uri);
    return data;
  }
}

export class FileInfoBuilder {
  path: string;
  name: string;
  size: number;
  bytes: Uint8Array;
  uri: string;

  constructor() {
    this.path = '';
    this.name = '';
    this.size = 0;
    this.bytes = new Uint8Array();
    this.uri = '';
  }

  withPath(path: string): FileInfoBuilder {
    this.path = path;
    return this;
  }

  withName(name: string): FileInfoBuilder {
    this.name = name;
    return this;
  }

  withSize(size: number): FileInfoBuilder {
    this.size = size;
    return this;
  }

  withData(bytes: Uint8Array): FileInfoBuilder {
    this.bytes = bytes;
    return this;
  }

  withUri(uri: string): FileInfoBuilder {
    this.uri = uri;
    return this;
  }

  build(): FileInfo {
    return new FileInfo(this.path, this.name, this.size, this.bytes, this.uri);
  }
}

