import { componentUtils, UIContext } from '@kit.ArkUI';

export class ComponentAttrUtils {
  public static getRectInfoById(context: UIContext, id: string, cornerRadius?: number): RectInfo_px | undefined {
    if (!context || !id) {
      return undefined;
    }

    let componentInfo: componentUtils.ComponentInfo = context.getComponentUtils().getRectangleById(id);

    if (!componentInfo) {
      return undefined;
    }

    let retRect: RectInfo_px = new RectInfo_px();
    const widthScaleGap = componentInfo.size.width * (1 - componentInfo.scale.x) / 2;
    const heightScaleGap = componentInfo.size.height * (1 - componentInfo.scale.y) / 2;
    retRect.left = componentInfo.translate.x + componentInfo.windowOffset.x + widthScaleGap;
    retRect.top = componentInfo.translate.y + componentInfo.windowOffset.y + heightScaleGap;
    let right = componentInfo.translate.x + componentInfo.windowOffset.x + componentInfo.size.width - widthScaleGap;
    let bottom =
      componentInfo.translate.y + componentInfo.windowOffset.y + componentInfo.size.height - heightScaleGap;
    retRect.width = right - retRect.left;
    retRect.height = bottom - retRect.top;
    return {
      left: retRect.left,
      top: retRect.top,
      width: retRect.width,
      height: retRect.height,
      cornerRadius: cornerRadius ?? 0
    }
  }

  public static getRectInfoByClickEvent(event: ClickEvent, cornerRadius?: number): RectInfo_px | undefined {
    if (!event) {
      return undefined;
    }

    let retRectInfo = new RectInfo_px();
    retRectInfo.left = vp2px(event.windowX - event.x);
    retRectInfo.top = vp2px(event.windowY - event.y);
    retRectInfo.width = vp2px(event.target.area.width as number);
    retRectInfo.height = vp2px(event.target.area.height as number);
    retRectInfo.cornerRadius = cornerRadius ?? 0;
    return retRectInfo;
  }
}

export class RectInfo_px {
  left: number = 0;
  top: number = 0;
  width: number = 0;
  height: number = 0;
  cornerRadius: number = 0;
}

