@Component
struct MainComponent {
  @Provide inputArray: Array<InputField> = Array(2);
  @Provide printStr: string = '';

  aboutToAppear(): void {
    this.inputArray[0] = {
      label: 'Age',
      inputStr: '',
      inputType: InputType.Number,
      maxLength: 4,
    }
    this.inputArray[1] = {
      label: 'Name',
      inputStr: '',
      inputType: InputType.Normal,
    }
  }

