import router from '@ohos.router'
import { UploadHistoryModel } from '../model/UploadHistoryModel';
import http from '@ohos.net.http';
import promptAction from '@ohos.promptAction';
import { HttpModel } from '../model/HttpModel';
import { Url } from '../const/Url';
import { DateUtils } from '../utils/DateUtils';
import { RouterHelper } from '../helper/RouteHelper';

@Entry
@Component
struct FileUploadHistoryPage {
  @State isRefreshing: boolean = false;
  @State itemList: UploadHistoryModel[] = [];

  private requestFileUploadHistoryList() {
    let httpRequest = http.createHttp();
    let url = Url.FILE_UPLOAD_HISTORY_LIST
    let requestOption: http.HttpRequestOptions = {
      method: http.RequestMethod.GET,
      readTimeout: 5000,
      connectTimeout: 5000
    }

    this.isRefreshing = true;

    httpRequest.request(url, requestOption, (err, data) => {
      this.isRefreshing = false;

      if (!err && data.responseCode == 200) {
        console.info('请求成功：' + JSON.stringify(data.result));
        let httpModel = JSON.parse(data.result as string) as HttpModel<UploadHistoryModel[]>
        let newList: UploadHistoryModel[] = httpModel.data
        this.itemList = newList
      } else {
        console.info('请求失败：' + JSON.stringify(err));
        promptAction.showToast({
          message: '请求失败',
          duration: 2000,
        });
        httpRequest.destroy();
      }
    });
  }

  aboutToAppear() {
    this.requestFileUploadHistoryList()
  }

  build() {
    Refresh({ refreshing: this.isRefreshing }) {
      List() {
        ForEach(this.itemList, (item: UploadHistoryModel) => {
          ListItem() {
            FileUploadHistoryItem({ item: item })
              .onClick(() => {
                RouterHelper.goImagePreview(item.url);
              })
          }
        })
      }
      .height('100%')
      .width('100%')
    }
    .height('100%')
    .width('100%')
    .onRefreshing(() => {
      this.requestFileUploadHistoryList()
    })
  }
}

@Component
struct FileUploadHistoryItem {
  @Prop item: UploadHistoryModel;

  build() {
    Column() {
      Row() {
        Image(this.item.url)
          .width(45)
          .height(45)

        Column() {
          Text(this.item.fileName)
            .fontSize(15)
