        }
        .margin(10)
        .layoutWeight(1)
        .alignItems(HorizontalAlign.Start)
        .justifyContent(FlexAlign.Center)
      }
      .alignItems(VerticalAlign.Center)
      .justifyContent(FlexAlign.Start)

      Divider()
        .width('100%')
        .height(0.5)
        .color('#ffe9f0f0')
    }
    .padding(15)
    .width("100%")
  }
}

