import fs from '@ohos.file.fs';
import CommonUtil from '../CommonUtil';
import { HiLog } from '../HiLog';

const TAG = 'FileUtil';

export default class FileUtil {
  public static closeSync(file: fs.File | number | undefined): void {
    if (!file) {
      HiLog.error(TAG, 'closeSync param is invalid.');
      return;
    }
    try {
      fs.closeSync(file);
      HiLog.info(TAG, 'closeSync success.');
    } catch (err) {
      HiLog.wrapError(TAG, err, 'closeSync failed');
    }
  }

  public static unlinkSync(uri: string): void {
    if (CommonUtil.isEmptyStr(uri)) {
      HiLog.error(TAG, 'unlinkSync param is invalid.');
      return;
    }
    try {
      const isExist = fs.accessSync(uri);
      if (isExist) {
        fs.unlinkSync(uri);
      }
      HiLog.info(TAG, 'unlinkSync success.');
    } catch (err) {
      HiLog.wrapError(TAG, err, 'unlinkSync failed');
    }
  }

  public static rmdirSync(uri: string): void {
    if (CommonUtil.isEmptyStr(uri)) {
      HiLog.error(TAG, 'removeDir: param is invalid.');
      return;
    }
    try {
      let res = fs.accessSync(uri);
      if (res) {
        fs.rmdirSync(uri);
      }
    } catch (err) {
      HiLog.wrapError(TAG, err, 'rmdirSync failed');
    }
  }

  public static async unlink(uri: string): Promise<void> {
    if (CommonUtil.isEmptyStr(uri)) {
      HiLog.error(TAG, 'unlink: param is invalid.');
      return;
    }
    try {
      await fs.unlink(uri);
    } catch (err) {
      HiLog.wrapError(TAG, err, 'unlink failed');
    }
  }
}

// CalendarPicker modifier 使用$r设置edgeAlign
import ConfigurationConstant from '@ohos.app.ability.ConfigurationConstant';
import common from '@ohos.app.ability.common';

class MyCalendarPickerModifier implements AttributeModifier<CalendarPickerAttribute> {
  public flag = false;
  applyNormalAttribute(instance: CalendarPickerAttribute): void {
    if (this.flag == false) {
      instance.edgeAlign(
        CalendarAlign.END,
        {
          dx: $r('app.float.calendar_picker_edge_align_offset'),
          dy: $r('app.float.calendar_picker_edge_align_offset')
        }
      )
    }
  }
}

@Entry
@Component
struct Index {
  @State calendarPickerModifier: MyCalendarPickerModifier = new MyCalendarPickerModifier();
  @State isDark: boolean = false;
  @State context: common.UIAbilityContext = getContext(this) as common.UIAbilityContext;


  build() {
    Column() {
      Button('flag: ' + this.calendarPickerModifier.flag)
        .onClick(() => {
          this.calendarPickerModifier.flag = !this.calendarPickerModifier.flag;
        })
        .margin(20)

      Button('isDark: ' + this.isDark)
        .onClick(() => {
          if (this.isDark) {
            this.isDark = !this.isDark;
            this.context.getApplicationContext().setColorMode(ConfigurationConstant.ColorMode.COLOR_MODE_LIGHT);
          } else {
            this.isDark = !this.isDark;
            this.context.getApplicationContext().setColorMode(ConfigurationConstant.ColorMode.COLOR_MODE_DARK);
          }
        })
        .margin(20)

      Text('modifier')
      CalendarPicker({
      })
        .attributeModifier(this.calendarPickerModifier)
        .onChange(() => {
          /*
           * switch the color mode
           */
          if (this.isDark) {
            this.isDark = !this.isDark;
            this.context.getApplicationContext().setColorMode(ConfigurationConstant.ColorMode.COLOR_MODE_LIGHT);
          } else {
            this.isDark = !this.isDark;
            this.context.getApplicationContext().setColorMode(ConfigurationConstant.ColorMode.COLOR_MODE_DARK);
          }
        })
    }
    .width('100%')
  }
}

