import prompt from '@ohos.prompt';
import deviceManager from '@ohos.distributedHardware.deviceManager'
import Logger from '../utils/Logger'

const TAG = 'RemoteDeviceModel'
let SUBSCRIBE_ID = 100

export class RemoteDeviceModel {
  public deviceList: Array<deviceManager.DeviceInfo> = []
  public discoverList: Array<deviceManager.DeviceInfo> = []
  private callback: () => void = null
  private authCallback: () => void = null
  private deviceManager: deviceManager.DeviceManager = undefined

  constructor() {
  }

  registerDeviceListCallback(callback) {
    if (typeof (this.deviceManager) !== 'undefined') {
      this.registerDeviceListCallbackImplement(callback)
      return
    }
    Logger.info(TAG, 'deviceManager.createDeviceManager begin')
    try {
      deviceManager.createDeviceManager("com.haoc.distributedvideoplayer", (error, value) => {
        if (error) {
          Logger.error(TAG, `createDeviceManager failed.${error}`)
          return
        }
        this.deviceManager = value
        this.registerDeviceListCallbackImplement(callback)
        Logger.info(TAG, `createDeviceManager callback returned, error= ${error} value= ${JSON.stringify(value)}`)
      })
    } catch (error) {
      Logger.error(TAG, `createDeviceManager throw error, code=${error.code} message=${error.message}`)
    }
    Logger.info(TAG, 'deviceManager.createDeviceManager end')
  }

  deviceStateChangeActionOnline(device) {
    this.deviceList[this.deviceList.length] = device
    try {
      this.deviceManager.getTrustedDeviceListSync()
      Logger.info(TAG, `online device list= ${JSON.stringify(this.deviceList)}`)
      this.callback()
      if (this.authCallback !== null) {
        this.authCallback()
        this.authCallback = null
      }
    } catch (error) {
      Logger.error(TAG, `getTrustedDeviceListSync throw error, code=${error.code} message=${error.message}`)
    }
  }

  deviceStateChangeActionOffline(device) {
    if (this.deviceList.length <= 0) {
      this.callback()
      return
    }
    for (let j = 0; j < this.deviceList.length; j++) {
      if (this.deviceList[j].deviceId === device.deviceId) {
        this.deviceList[j] = device
        break
      }
    }
    Logger.info(TAG, `offline, device list= ${JSON.stringify(this.deviceList)}`)
    this.callback()
  }

  registerDeviceListCallbackImplement(callback) {
    Logger.info(TAG, 'registerDeviceListCallback')
    this.callback = callback
    if (this.deviceManager === undefined) {
      Logger.error(TAG, 'deviceManager has not initialized')
      this.callback()
      return
    }
    Logger.info(TAG, 'getTrustedDeviceListSync begin')
    try {
      let list = this.deviceManager.getTrustedDeviceListSync()
      Logger.info(TAG, `getTrustedDeviceListSync end, deviceList= ${JSON.stringify(list)}`)
      if (typeof (list) != 'undefined' && typeof (list.length) != 'undefined') {
        this.deviceList = list
      }
    } catch (error) {
      Logger.error(TAG, `getTrustedDeviceListSync throw error, code=${error.code} message=${error.message}`)
    }

    this.callback()
    Logger.info(TAG, `callback finished devices = ${JSON.stringify(this.deviceList)}`)
    try {
      this.deviceManager.on('deviceStateChange', (data) => {
        if (data === null) {
          return
        }
        Logger.info(TAG, `deviceStateChange data= ${JSON.stringify(data)}`)
        switch (data.action) {
          case deviceManager.DeviceStateChangeAction.ONLINE:
            this.deviceStateChangeActionOnline(data.device)
            break;
          case deviceManager.DeviceStateChangeAction.READY:
            this.discoverList = []
            this.deviceList.push(data.device)
            this.callback()
            try {
              let list = this.deviceManager.getTrustedDeviceListSync()
              if (typeof (list) !== 'undefined' && typeof (list.length) !== 'undefined') {
                this.deviceList = list
              }
            } catch (error) {
              Logger.error(TAG, `getTrustedDeviceListSync throw error, code=${error.code} message=${error.message}`)
            }
            this.callback()
            break;
          case deviceManager.DeviceStateChangeAction.OFFLINE:
          case deviceManager.DeviceStateChangeAction.CHANGE:
            this.deviceStateChangeActionOffline(data.device)
            break
          default:
            break
        }
      })
      this.deviceManager.on('deviceFound', (data) => {
        if (data === null) {
          return
        }
        Logger.info(TAG, `deviceFound data=${JSON.stringify(data)}`)
        this.deviceFound(data)
      })
      this.deviceManager.on('discoverFail', (data) => {
        Logger.info(TAG, `discoverFail data= ${JSON.stringify(data)}`)
      })
      this.deviceManager.on('serviceDie', () => {
        Logger.error(TAG, 'serviceDie')
      })
    } catch (error) {
      Logger.error(TAG, `on throw error, code=${error.code} message=${error.message}`)
    }
    this.startDeviceDiscovery()
  }

  deviceFound(data) {
    for (var i = 0;i < this.discoverList.length; i++) {
      if (this.discoverList[i].deviceId === data.device.deviceId) {
        Logger.info(TAG, 'device founded ignored')
        return
      }
    }
    this.discoverList[this.discoverList.length] = data.device
    Logger.info(TAG, `deviceFound self.discoverList= ${JSON.stringify(this.discoverList)}`)
    this.callback()
  }

  /**
   * 通过SUBSCRIBE_ID搜索分布式组网内的设备
   */
  startDeviceDiscovery() {
    // 生成发现标识，随机数确保每次调用发现接口的标识不一致,且SUBSCRIBE_ID在0到65536之间
    SUBSCRIBE_ID = Math.floor(65536 * Math.random())
    let info = {
      subscribeId: SUBSCRIBE_ID, // 发现标识，用于标识不同的发现周期
      mode: 0xAA, // 主动模式
      medium: 2, // WiFi发现类型
      freq: 2, // 高频率
      isSameAccount: false, // 是否同账号
      isWakeRemote: true, // 是否唤醒设备
      capability: 0 // DDMP能力
    }
    Logger.info(TAG, `startDeviceDiscovery ${SUBSCRIBE_ID}`)
    // 当有设备发现时，通过deviceFound回调通知给应用程序
    try {
      this.deviceManager.startDeviceDiscovery(info)
    } catch (error) {
      Logger.error(TAG, `startDeviceDiscovery throw error, code=${error.code} message=${error.message}`)
    }
  }

  unregisterDeviceListCallback() {
    try {
      Logger.info(TAG, `stopDeviceDiscovery ${SUBSCRIBE_ID}`)
      this.deviceManager.stopDeviceDiscovery(SUBSCRIBE_ID);
      this.deviceManager.off('deviceStateChange')
      this.deviceManager.off('deviceFound')
      this.deviceManager.off('discoverFail')
      this.deviceManager.off('serviceDie')
      this.deviceList = []
      this.discoverList = []
    } catch (error) {
      Logger.error(TAG, `off throw error, code=${error.code} message=${error.message}`)
    }
  }

  authenticateDevice(device, callBack) {
    Logger.info(TAG, `authenticateDevice ${JSON.stringify(device)}`)
    for (let i = 0; i < this.discoverList.length; i++) {
      if (this.discoverList[i].deviceId !== device.deviceId) {
        continue
      }
      let extraInfo = {
        'targetPkgName': 'com.haoc.distributedvideoplayer',
        'appName': 'Distributed VideoPlayer',
        'appDescription': 'Distributed VideoPlayer',
        'business': '0'
      }
      let authParam = {
        'authType': 1, //认证类型： 1 - 无账号PIN码认证
        'appIcon': '',
        'appThumbnail': '',
        'extraInfo': extraInfo
      }
      try {
        this.deviceManager.authenticateDevice(device, authParam, (err, data) => {
          if (err) {
            Logger.error(TAG, `authenticateDevice error: ${JSON.stringify(err)}`)
            this.authCallback = null
            prompt.showToast({ message: `设备连接失败 ${err.message}` })
            return
          }
          Logger.info(TAG, `authenticateDevice succeed: ${JSON.stringify(data)}`)
          this.authCallback = callBack
        })
      } catch (error) {
        Logger.error(TAG, `authenticateDevice throw error, code=${error.code} message=${error.message}`)
      }
    }
  }
}

import { IntroductionTitle } from '../../../../common/IntroductionTitle';
import { GridItemDrag } from './component/GridItemDrag';
import { HyperlinkDrag } from './component/HyperlinkDrag';
import { ImageDrag } from './component/ImageDrag';
import { ListItemDrag } from './component/ListItemDrag';
import { TextDrag } from './component/TextDrag';
import { VideoDrag } from './component/VideoDrag';
import { router } from '@kit.ArkUI';

@Extend(Row)
function rowStyle() {
  .width('100%')
  .padding(16)
  .borderRadius(24)
  .backgroundColor(Color.White)
}

@Extend(Column)
function columnStyle() {
  .width('100%')
  .padding(16)
  .borderRadius(24)
  .backgroundColor(Color.White)
}

@Entry
@Component
struct DragEventSample {
  // Receivable
  @State isAreaValid: boolean = false;
  @State backgroundBlur: boolean = false;
  @State hideLeftIcon: boolean = true;
  @State hideRightIcon: boolean = true;
  @State showOkBadge: boolean = false;
  @State showCancelBadge: boolean = false;
  @State leftIconWidth: number = 64;
  @State rightIconWidth: number = 64;

  // Customize the content displayed during dragging
  @Builder
  pixelMapBuilder() {
    Stack({ alignContent: Alignment.TopEnd }) {
      Image($r('app.media.icon'))
        .width(64)
        .aspectRatio(1)
        .margin(16)
      if (this.isAreaValid) {
        Image($r('app.media.ic_ok'))
          .width(32)
          .aspectRatio(1)
          .fillColor(Color.Green)
      } else {
        Image($r('app.media.ic_cancel'))
          .width(32)
          .aspectRatio(1)
          .fillColor(Color.Red)
      }
    }
  }

  @Builder
  menuBuilder() {
    Column() {
      Text($r('app.string.drag_drop_action_tips'))
        .fontSize(16)
    }
    .alignItems(HorizontalAlign.Center)
    .justifyContent(FlexAlign.Center)
    .padding(16)
    .borderRadius(12)
  }

  build() {
    Column() {
      Column() {
        Row() {
          Image($r('app.media.ic_back'))
            .width(20)
            .height(20)
            .margin({ left: 26 })
            .objectFit(ImageFit.Contain)
            .onClick(() => {
              router.back()
            })
            .id('backBtn')
          Text($r('app.string.drag_drop_event'))
            .fontSize(20)
            .layoutWeight(1)
            .margin({ left: 16 })
            .align(Alignment.Start)
          Blank()
        }
        .height(56)
        .width('100%')
      }

      Scroll() {
        Column() {
          IntroductionTitle({ introduction: $r('app.string.drag_drop_action_tips') })
          Stack() {
            Row() {
              // Green acceptable area on the left
              Row() {
                Stack({ alignContent: Alignment.TopEnd }) {
                  Image($r('app.media.icon'))
                    .width(this.leftIconWidth)
                    .aspectRatio(1)
                    .margin(this.leftIconWidth / 4)
                    .visibility(this.hideLeftIcon ? Visibility.None : Visibility.Visible)
                    .animation({
                      onFinish: () => {
                        this.showOkBadge = false;
                      }
                    })
                  if (this.showOkBadge) {
                    Image($r('app.media.ic_ok'))
                      .width(this.leftIconWidth / 2)
                      .aspectRatio(1)
                      .fillColor(Color.Green)
                      .animation({})
                  }
                }
              }
              .id('leftArea')
              .width('45%')
              .height(200)
              .backgroundColor($r('app.color.background_green'))
              .justifyContent(FlexAlign.Center)
              .onDragEnter(() => {
                this.isAreaValid = true;
              })
              .onDragMove(() => {
                this.isAreaValid = true;
              })
              .onDragLeave(() => {
                this.isAreaValid = false;
              })
              .onDrop(() => {
                this.hideLeftIcon = false;
                this.showOkBadge = true;
                this.leftIconWidth = 48;
              })

              Blank()

              // The red unacceptable area on the right
              Row() {
                Stack({ alignContent: Alignment.TopEnd }) {
                  Image($r('app.media.icon'))
                    .width(this.rightIconWidth)
                    .aspectRatio(1)
                    .margin(this.rightIconWidth / 4)
                    .visibility(this.hideRightIcon ? Visibility.None : Visibility.Visible)
                    .animation({
                      onFinish: () => {
                        this.hideRightIcon = true;
                        this.showCancelBadge = false;
                        this.rightIconWidth = 64;
                      }
                    })
                  if (this.showCancelBadge) {
                    Image($r('app.media.ic_cancel'))
                      .width(this.rightIconWidth / 2)
                      .aspectRatio(1)
                      .fillColor('#FF0000')
                      .animation({})
                  }
                }
              }
              .id('rightArea')
              .width('45%')
              .height(200)
              .backgroundColor($r('app.color.background_pink'))
              .justifyContent(FlexAlign.Center)
              .onDragEnter(() => {
                this.isAreaValid = false;
              })
              .onDragMove(() => {
                this.isAreaValid = false;
              })
              .onDrop(() => {
                this.hideRightIcon = false;
                this.showCancelBadge = true;
                this.rightIconWidth = 72;
              })
            }
            .rowStyle()

            // Fuzzy Layer
            Row() {
              Blank()

              // Middle drag icon
              Column() {
                if (this.hideLeftIcon) {
                  Row() {
                    Image($r('app.media.icon'))
                      .id('icon')
                      .width(48)
                      .aspectRatio(1)
                      .draggable(false)
                  }
                  .bindContextMenu(this.menuBuilder(), ResponseType.LongPress)
                  .onDragStart(() => {
                    this.backgroundBlur = false;
                    // Close menu while dragging
                    ContextMenu.close();
                    return this.pixelMapBuilder;
                  })
                  .onTouch((event: TouchEvent) => {
                    if (event.type === TouchType.Down) {
                      this.backgroundBlur = true;
                    }
                  })
                }
              }
              .width('10%')
              .height(200)
              .justifyContent(FlexAlign.Center)

              Blank()
            }
            .width('100%')
            .padding(16)
            .borderRadius(24)
            .backgroundBlurStyle(this.backgroundBlur ? BlurStyle.BACKGROUND_THIN : 0)
            .onClick(() => {
              this.backgroundBlur = false;
            })
          }

          IntroductionTitle({ introduction: $r('app.string.component_drag_and_drop') })
          Column() {
            IntroductionTitle({ introduction: $r('app.string.griditem') })
            GridItemDrag()

            IntroductionTitle({ introduction: $r('app.string.listitem') })
            ListItemDrag()

            IntroductionTitle({ introduction: $r('app.string.hyperlink') })
            HyperlinkDrag()

            IntroductionTitle({ introduction: $r('app.string.text') })
            TextDrag()

            IntroductionTitle({ introduction: $r('app.string.image') })
            ImageDrag()

            IntroductionTitle({ introduction: $r('app.string.video') })
            VideoDrag()
          }
          .id('column')
          .columnStyle()
          .margin({ bottom: 80 })
        }
      }
      .padding({ left: 12, right: 12 })
    }
    .backgroundColor($r('app.color.background_shallow_grey'))
  }
}
